import React, { useState } from 'react';
import { useNavigate } from 'react-router-dom';
import './LearningDashboard.css';

function LearningDashboard() {
  const navigate = useNavigate();
  const [sortOrder, setSortOrder] = useState('progress'); 

  const goToCatalogue = () => {
    navigate('/course-catalogue');
  };

  const [courses, setCourses] = useState([]);

    useEffect(() => {
      fetch('http://localhost:8000/')
        .then(response => response.json())
        .then(data => setCourses(data))
        .catch(error => console.error('Error fetching courses:', error));
    }, []);


  const handleSortChange = (event) => {
    setSortOrder(event.target.value);
  };


  return (
    <div className="dashboard-container">
      <h1>Learning Dashboard</h1>
      <p>Welcome to your learning dashboard!</p>
      <p>Manage your courses and progress here.</p>
      <div>
        <label>Sort Courses By:</label>
        <div onChange={handleSortChange}>
          <input type="radio" value="name" name="sort" checked={sortOrder === 'name'} /> Name
          <input type="radio" value="progress" name="sort" checked={sortOrder === 'progress'} /> Progress
          <input type="radio" value="recent" name="sort" checked={sortOrder === 'recent'} /> Recently Accessed
        </div>
      </div>

      <ul>
        {courses.map((course) => (
          <li key={course.id} className="course-item">
            <div className="course-title">
              <input type="checkbox" id={`course-${course.id}`} name="selectedCourses" value={course.id} />
              <label htmlFor={`course-${course.id}`}><h2>{course.name}</h2></label>
            </div>
            <p>Progress: {course.progress}</p>
            <button className="progress-button">Continue Learning</button>
            <input type="text" placeholder="Add notes..." className="course-notes" />
          </li>
        ))}
      </ul>

      <button onClick={goToCatalogue} className="explore-button">Explore Courses</button>

      <div className="image-container">
        <img src="https://dashboardlearning.com/wp-content/uploads/2022/02/about-dashboard-2.png"/>
      </div>
    </div>
  );
}

export default LearningDashboard;
